custom_defined_behaviors:
macros {
  // Sends ctrl-a
  tmux_scroll: tmux_scroll{
    label = "tmux_scroll";
    compatible = "zmk,behavior-macro";
    #binding-cells = <0>;
    wait-ms = <40>;
    tap-ms = <40>;
    bindings
    = <&kp LC(A)>
    , <&kp LBKT>
    ;
  };

  // Sends "../"
  up_directory: up_directory{
    label = "up_directory";
    compatible = "zmk,behavior-macro";
    #binding-cells = <0>;
    bindings
    = <&macro_tap &kp DOT &kp DOT &kp SLASH>
    ;
  };

  // Sends {} followed by left arrow
  brackets_pair: brackets_pair{
    label = "brackets_pair";
    compatible = "zmk,behavior-macro";
    #binding-cells = <0>;
    bindings
    = <&macro_tap &kp LBKT &kp RBKT &kp LEFT>
    ;
  };

  // Sends [] followed by left arrow
  braces_pair: braces_pair{
    label = "braces_pair";
    compatible = "zmk,behavior-macro";
    #binding-cells = <0>;
    bindings
    = <&macro_tap &kp LS(LBKT) &kp LS(RBKT) &kp LEFT>
    ;
  };

  // Sends () followed by left arrow
  parens_pair: parens_pair{
    label = "parens_pair";
    compatible = "zmk,behavior-macro";
    #binding-cells = <0>;
    bindings
    = <&macro_tap &kp LPAR &kp RPAR &kp LEFT>
    ;
  };

  // Sends <> followed by left arrow
  lt_gt_pair: lt_gt_pair{
    label = "lt_gt_pair";
    compatible = "zmk,behavior-macro";
    #binding-cells = <0>;
    bindings
    = <&macro_tap &kp LT &kp GT &kp LEFT>
    ;
  };

  // Writes a helm-style comment and places cursor in the center: {{- /* | */ -}}
  helm_comment: helm_comment{
    label = "helm_comment";
    compatible = "zmk,behavior-macro";
    #binding-cells = <0>;
    bindings
    = <&macro_tap &kp LS(LBKT) &kp LS(LBKT) &kp MINUS &kp SPACE &kp SLASH &kp STAR &kp SPACE &kp SPACE &kp SPACE &kp STAR &kp SLASH &kp SPACE &kp MINUS &kp LS(RBKT) &kp LS(RBKT) &kp LEFT &kp LEFT &kp LEFT
&kp LEFT &kp LEFT &kp LEFT &kp LEFT &kp LEFT>
    ;
  };
};

behaviors {
  // Send regular shift on tap, enable CAPSWORD on double-tap
  shift_capsword: shift_capsword {
    label = "SHIFT_CAPSWORD";
    compatible = "zmk,behavior-tap-dance";
    #binding-cells = <0>;
    tapping-term-ms = <200>;
    bindings = <&kp LSHIFT>, <&caps_word>;
  };

  // Send tab on tap, send ctrl-alt-tab on double-tap
  auto_tab: auto_tab {
    compatible = "zmk,behavior-hold-tap";
    #binding-cells = <2>;
    tapping_term_ms = <500>;
    quick_tap_ms = <0>;
    global-quick-tap;
    flavor = "tap-preferred";
    bindings = <&kp>, <&kp>;
  };

  // Momentary layer on hold, sticky layer on tap
  #define MO_SL(layer) &mo_sl layer layer
  mo_sl: behavior_mo_sl {
    compatible = "zmk,behavior-hold-tap";
    #binding-cells = <2>;
    flavor = "hold-preferred";
    tapping-term-ms = <200>;
    bindings = <&mo>, <&sl>;
  };
};

combos {
  compatible = "zmk,combos";
  combo_updir {
    timeout-ms = <50>;
    key-positions = <61 62>; // "./"
    bindings = <&up_directory>;
  };

  combo_braces_pair {
    timeout-ms = <50>;
    key-positions = <25 26>; // "{}"
    bindings = <&braces_pair>;
    layers = <3>;
  };

  combo_brackets_pair {
    timeout-ms = <50>;
    key-positions = <37 38>; // "[]"
    bindings = <&brackets_pair>;
    layers = <3>;
  };

  combo_parens_pair {
    timeout-ms = <50>;
    key-positions = <49 50>; // "()"
    bindings = <&parens_pair>;
    layers = <3>;
  };

  combo_lt_gt_pair {
    timeout-ms = <50>;
    key-positions = <60 61>; // "<>"
    bindings = <&lt_gt_pair>;
    layers = <3>;
  };

  combo_helm_comment {
    timeout-ms = <50>;
    key-positions = <40 25 43>; // "HEL"
    bindings = <&helm_comment>;
    layers = <3>;
  };
};

custom_devicetree:
&caps_word {
  continue-list = <
    UNDERSCORE MINUS
    BACKSPACE DELETE
    N1 N2 N3 N4 N5 N6 N7 N8 N9 N9 N0
  >;
};

&sl {
  release-after-ms = <1000>;
};
